$(document).ready(function(){$(".app-list .environment").hide().velocity("transition.swoopIn",{stagger:100}),$(".js-create-project, .repo-settings").click(function(){$("#new-project").velocity("transition.expandIn",{duration:300})}),$("#new-project .close").click(function(){$("#new-project").velocity("transition.expandOut",{duration:300})}),$("#new-project form").submit(function(){$("#new-project").velocity("transition.expandOut",{duration:300}),notif("success","You add x project")}),$(".js-more-environment").click(function(){newEnv=$("#new-project .add-environment-list li:first-child").clone(),$(newEnv).appendTo("#new-project .add-environment-list").show()}),$(".js-update-repos").click(function(){notif("error","No update available")}),$(".environment [data-action]").click(function(){"stop"===$(this).attr("data-action")?$(this).attr("data-action","deploy").text("deploy").prev(".status").attr("data-state","stop"):"deploy"===$(this).attr("data-action")&&$(this).attr("data-action","stop").text("stop").prev(".status").attr("data-state","run")})});